import { Navbar, Container, Nav, Card, DropdownButton, Dropdown } from 'react-bootstrap';
import React, { useEffect, useState } from 'react'
import axios from 'axios';
import cheerio from 'cheerio';
import './App.css';
import './main.css';
import jquery from 'jquery';
import $ from 'jquery';



function App() {

	var [top10, top10_change] = useState([]);
	var [page, page_change] = useState(1);
	var [info, info_change] = useState(Array.from({length : 10}, () => false));

	// const getHtml = async() => {
