	useEffect(() => {

		try {
			axios.get("http://localhost:8080/public/top10")
			.then(response => {
				var json = response.data.result;
				top10_change(json);
				console.log(json);
				// var json = resonse.data.boxOfficeResult.dailyBoxOfficeList
			})

		} catch {
