	// 	}).catch( error => {
	// 		console.log(error);
	// 	})

	// }, [page]);
	}, [page]);

	return (
		<div className="App">
			<Navbar bg="dark" variant="dark">
				<Container className='text-center'>
					<Navbar.Brand href="#home">MOVIEDIC</Navbar.Brand>
						<Nav className="me-auto">
						<Nav.Link href="#home">HOME</Nav.Link>
						<Nav.Link href="#features">BOXOFFICE</Nav.Link>
						<Nav.Link href="#pricing">HISTORY</Nav.Link>
						<Nav.Link href="#pricing">MY</Nav.Link>
					</Nav>
				</Container>
			</Navbar>


			{/* <CaroselSet /> */}
			{/* <button onClick={() => {
				axios.get('local')
			}}></button> */}

			<div className=" mt-4">
				<h4 className="text-center">TODAY BOXOFFICE</h4>
				<div className="boxoffice mt-4">
					<div className='boxoffice-cardset'>
						{
							top10.map((movie, i) => {
								return (
									<Card className="boxoffice-card"
									onMouseEnter={() => {
										var newArr = [...info];
										newArr[i] = true;
										info_change(newArr);
									}} onMouseLeave={() => {
										var newArr = [...info];
										newArr[i] = false;
										info_change(newArr);
									}}key={i}>
										<Card.Img variant="top" className="boxoffice-img" src={movie.posterUrl} />
										<Card.Text className={'boxoffice-rank card-' + i}>{movie.id}</Card.Text>
										{
											info[i]
											? <Card.Body className="boxoffice-bg">
												<Card.Title className="boxoffice-title">
													<div>{movie.title}</div>
													<div className="boxoffice-subtitle">{movie.subTitle}</div>
												</Card.Title>

												<Card.Text className='boxoffice-content'> {movie.info[0]} | {movie.info[2]}</Card.Text>
												<Card.Text className='boxoffice-content'>{movie.info[4]}</Card.Text>
												<DropdownButton id="dropdown-basic-button" title="예매하기">
													<Dropdown.Item href="#/action-1">CGV</Dropdown.Item>
													<Dropdown.Item href="#/action-2">LOTTECINEMA</Dropdown.Item>
													<Dropdown.Item href="#/action-3">MEGA BOX</Dropdown.Item>
												</DropdownButton>
											</Card.Body>
											: null
										}
									</Card>
								)
							})
						}
					</div>
					<div className='boxoffice-btn'>
						<button className="btn before" onClick={() => {

