//firebase setting
// Import the functions you need from the SDKs you need
import firebase from "firebase/app";
import 'firebase/firestore'
import 'firebase/auth'
// import { getAnalytics } from "firebase/analytics";
// import { GoogleAuthProvider } from "firebase/auth"
// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyCTgOH4hH8GdM9FRhyIdNb4Rzlg5_QThHc",
  authDomain: "movidic-login.firebaseapp.com",
  projectId: "movidic-login",
  storageBucket: "movidic-login.appspot.com",
  messagingSenderId: "920799641651",
  appId: "1:920799641651:web:5b653f87f106f18a5b2346",
  measurementId: "G-7MPDCRLKED"
};

// Initialize Firebase
initializeApp(firebaseConfig);

export const auth = firebase.auth();
export const firestore = firebase.firestore();

const provider = new firebase.auth.GoogleAuthProvider();
// GoogleAuthProvider 클래스를 authentication 라이브러리에서 사용할 수 있도록 불러오는 코드

provider.setCostomParameters({prompt : 'select_account'});
// signIn 이랑 authentication을 위해서 GoogleAuthProvider를 사용할 때마다 구글 팝업을 띄우기를 원한다는 의미

export const signInWithGoogle = () => auth.signInWithPopup(provider);
// signInWithPopup 메소드는 여러 파라미터를 받을 수 있다. 트위터, 페이스북, 깃허브 등으로도 로그인이 필요할 수도 있으므로.
// 여기에서는 google로 signIn할 것이기 때문에, 파라미터로 위에서 정의한 provider를 넣어준다.

export default firebase;
