import { Navbar, Container, Nav} from 'react-bootstrap';
import React, { useEffect, useState } from 'react'
import { Route } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import whale from './img/whale.png';

import Boxoffice from './main.js';
import Search from './search.js';
import History from './history.js';
// import firebase from './firebase.js';

// import { auth } from './firebase/firebase.utils';

function App() {

	var [loginModal, setLoginModal] = useState(true);
	var [loginState, setLoginState] = useState(false);
	var [userInfo, setUserInfo] = useState({});

	useEffect(() => {
		auth.onAuthStateChanged(user => {
			setUserInfo(user);
		})
	}, [loginState])
	return (
		<div className="App">
			<Navbar bg="dark" variant="dark">
				<Container className='text-center'>
						<Navbar.Brand href="/">
							<img src={whale} style={{width: '40px', height : '40px', marginRight : '10px'}} />
							<span>MOVIDIC</span>
						</Navbar.Brand>
						<Navbar.Collapse id="responsive-navbar-nav">
							<Nav className="me-auto">
								<Nav.Link href="/search">SEARCH</Nav.Link>
								<Nav.Link href="/history">HISTORY</Nav.Link>
								<Nav.Link href="#pricing">MY</Nav.Link>
							</Nav>
							<Nav>
								{
									loginState
									? <button className='btn btn-primary'>LOGOUT</button>
									: <button className='btn btn-primary' onClick={() => {
										setLoginModal(true)
									}}>LOGIN</button>
								}

							</Nav>
						</Navbar.Collapse>

				</Container>
			</Navbar>


			{
				loginModal
				? <div>
					<form onSubmit={onSubmit}>
					<input name="email" type="email" placeholder="Email" required value={email} onChange={onChange}/>
					<input name="password" type="password" placeholder="password" required value={password} onChange={onChange}/>
					<input type="submit" value={ newAccount ? "Create Account" : "Login" } />
					</form>
					<span onClick={toggleAccount}>{newAccount ? "Login" : "Create Account"}</span>
					<button name="google" onClick={onGoogleClick}>구글 계정으로 로그인</button>
				</div>
				: null
			}

			<Route exact path='/'>
				<Boxoffice />
			</ Route>

			<Route exact path='/search'>
				<Search />
			</Route>

			<Route exact path='/history'>
				<History />
			</Route>

			<div className='footer'>
				<p>CopyRight By Sangwoo</p>
			</div>
		</div>
	);
}



export default App;
