import { Form, Button, Row, Col, Accordion, Card } from 'react-bootstrap';
import React, { useState }from 'react';
import DatePicker from "react-datepicker";
import axios from 'axios';

import no from './img/noimage.png';


import './App.css';
import "react-datepicker/dist/react-datepicker.css";

function Search() {
    
    
    var [genre, genre_change] = useState({'드라마' : 1, '판타지' : 2,
        '서부': 3, '공포' : 4, '로맨스': 5, '모험':  6, '스릴러': 7, '느와르' : 8, '컬트' : 9, '다큐멘터리' : 10, '코미디' : 11, '가족' : 12, '미스터리' : 13, '전쟁' : 14, '애니메이션' : 15, '범죄' : 16, '뮤지컬' : 17, 'SF' : 18, '액션' : 19, '무협' : 20, '에로' : 21, '서스펜스' : 22, '서사' : 23, '블랙코미디' : 24, '실험' : 25, '영화카툰' : 26, '영화음악' : 27, '영화패러디포스터' : 28})
    
    var [startDate, setStartDate] = useState(new Date());
    var [endDate, setEndDate] = useState(new Date());
    var [selectGenre, setSelectGenre] = useState(0);
    var [searchResult, searchResult_change] = useState([])
    var [showResult, showResult_change] = useState(false);

    
    return (
        <div className='container mt-4'>
            <div className='search-box'>
                <Form>
                    <Form.Group as={Row} className="mb-3" controlId="">
                        <Form.Label column xs={2} style={{borderRight : '1px solid white'}}>제 목</Form.Label>
                        <Col xs={10}>
                            <Form.Control type="text" placeholder="Search..." />
                        </Col>
                    </Form.Group>
                    
                        
                    <Accordion>
                        <Accordion.Item eventKey="0" >
                            <Accordion.Header className='search-detail'>Search Detail</Accordion.Header>
                            <Accordion.Body className='search-detail'>
                                <Form.Group as={Row} className="mb-3 genreGroup" controlId="">
                                    <Form.Label column  xs={2} style={{ borderRight : '1px solid #212529'}}>장 르</Form.Label>
                                    <Col key={'inline-radio'} xs={10} style={{textAlign : 'left'}} >
                                        {
                                            Object.keys(genre).map(function(g, i ){
                                                return <Form.Check type={'radio'} inline label={g} name="group1" id={'inline-radio-' + i} className="genreName" key={i} />
                                            })
                                        }
                                    </Col>
                                </Form.Group>
                                <Form.Group as={Row} className="mb-3 genreGroup" controlId="">
                                    <Form.Label column xs={2} style={{ borderRight : '1px solid #212529'}}>개봉연도</Form.Label>
                                    <Col xs={10} className='mt-2 search-year'>
                                        <div className="datepicker-content">
                                            <DatePicker 
                                                selected={startDate}
                                                onChange={(date) => {
                                                    if (date > endDate){
                                                        alert('잘못된 날짜 설정입니다.');
                                                    } else {
                                                        setStartDate(date)
                                                    }
                                                }}
                                                showYearPicker
                                                dateFormat="yyyy"/>
                                        </div>
                                        <div className="datepicker-style datepicker-content" > 년 ~</div>
                                        <div className="datepicker-content">
                                            <DatePicker 
                                                selected={endDate}
                                                onChange={(date) => {
                                                    if (date < startDate){
                                                        alert('잘못된 날짜 설정입니다.');
                                                    } else {
                                                        setEndDate(date);
                                                    }
                                                }}
                                                showYearPicker
                                                dateFormat="yyyy"/>
                                        </div>
                                        <div className="datepicker-style datepicker-content" > 년</div>
                                    </Col>
                                </Form.Group>
                            </Accordion.Body>
                        </Accordion.Item>
                    </Accordion>

                    <Button variant="primary" className="mt-3 search-result" onClick={() => {

                        axios.get('/public/searchList')
                        .then(res => {
                            searchResult_change(res.data.result);
                            showResult_change(true);
                            // console.log(searchResult);
                        });
                        
                    }}
                    >Search</Button>
                </Form>

            </div>
            
            {
                showResult
                ? <SearchResult result={searchResult}/>
                : null
            }
            
        </div>
    )
}

function SearchResult(props) {

    var [searchResult, searchResult_change] = useState(props.result);
    return (
        <div className='container mt-5'>
            <div>
                <h2 style={{float:'left', margin:'0'}}>Search Result</h2>
                <p style={{float:'right'}}>총 <span className='h3 text-primary'>{searchResult.length}</span> 개</p>
            </div>
            <div style={{clear : 'both'}}></div>
            <hr style={{marginTop : '0'}}/>

            <div className='row searchResult-list'>
                <Row style={{margin : 'auto'}}>
                    {
                        searchResult.map(function(m, i) {
                            return (
                            <Col className="mt-3 mb-3" xs={6} md={4} lg={3} key={i}>
                                <Movie source={m}/>
                            </Col>
                            )
                        })
                    }
                </Row>
            </div>
        </div>
    )
}


function Movie(props) {


    return (
        <div>
            <Card  className="searchReuslt_movie">
                    <Card.Img variant="top" src={ props.source.image === "" ? no : props.source.image } />
                    <Card.Body>
                        <Card.Title>{props.source.title}</Card.Title>
                        <Card.Title>{props.source.subtitle}</Card.Title>
                        <Card.Text>{props.source.director}</Card.Text>
                        <Card.Text>{props.source.actor}</Card.Text>
                        <Button variant="primary" href={props.source.link}>Detail</Button>
                    </Card.Body>
                </Card>
        </div>
    )

}


export default Search;
