import { Navbar, Container, Nav, Modal, Button} from 'react-bootstrap';
import React, { useEffect, useState } from 'react'
import { Route } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import whale from './img/whale.png';

import Boxoffice from './main.js';
import Search from './search.js';
import History from './history.js';
import { authService, firebaseInstance } from './fbase';

function App() {

	var [loginModal, setLoginModal] = useState(false);
	var [loginState, setLoginState] = useState(false);
	var [userInfo, setUserInfo] = useState({});

	return (
		<div className="App">
			<Navbar bg="dark" variant="dark">
				<Container className='text-center'>
						<Navbar.Brand href="/">
							<img src={whale} style={{width: '40px', height : '40px', marginRight : '10px'}} />
							<span>MOVIDIC</span>
						</Navbar.Brand>
						<Navbar.Collapse id="responsive-navbar-nav">
							<Nav className="me-auto">
								<Nav.Link href="/search">SEARCH</Nav.Link>
								<Nav.Link href="/history">HISTORY</Nav.Link>
								<Nav.Link href="#pricing">MY</Nav.Link>
							</Nav>
							<Nav>
								{
									loginState
									? <button className='btn btn-primary'>LOGOUT</button>
									: <button className='btn btn-primary' onClick={() => setLoginModal(true)}>LOGIN</button>
								}

							</Nav>
						</Navbar.Collapse>

				</Container>
			</Navbar>


			< Login show = {loginModal} onHide={() => setLoginModal(false)}/>

			<Route exact path='/'>
				<Boxoffice />
			</ Route>

			<Route exact path='/search'>
				<Search />
			</Route>

			<Route exact path='/history'>
				<History />
			</Route>

			<div className='footer'>
				<p>CopyRight By Sangwoo</p>
			</div>
		</div>
	);
}

function Login(props) {

	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [newAcc, setNewAcc] = useState(true);

	const onChange = (e) => {
		const {target : {name, value}} = e;
		if (name = 'email'){
			setEmail(value)
		} else if (name = 'password') {
			setPassword(value);
		}
	}

	const onSubmit = async (e) => {
		e.preventDefault();
		try {
			let data;
			if (newAcc) {
				// create account
				data = await authService.createUserWithEmailAndPassword(email, password);
			  } else {
				// login
				data = await authService.signInWithEmailAndPassword(email, password);
			  }
			console.log(data);
		} catch (err) {
			console.log(err);
		}
	}

	const onGoogleClick = async (event) => {
		const {target: {name}} = event;
		let provider;
		if (name === 'google') {
		  provider = new firebaseInstance.auth.GoogleAuthProvider();
		}
		const data = await authService.signInWithPopup(provider);
		console.log(data);
	}

	// const onLogOutClick = () => {
	// 	authService.signOut();
	// }

	const toggleAccount = () => setNewAcc((prev) => !prev);

	return (
		<Modal
		{...props}
		className='login-modal'
		size="lg"
		aria-labelledby="contained-modal-title-vcenter"
		centered
		>
			<Modal.Header closeButton>
				<Modal.Title id="contained-modal-title-vcenter">Login</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<button className='btn btn-primary' onClick={toggleAccount}>{newAcc ? "Login" : "Create Account"}</button>

				<form onSubmit={onSubmit}>
					<input className='login-input' name="email" type="email" placeholder="Email" required value={email} onChange={onChange}/>
					<input className='login-input' type="password" placeholder="password" required value={password} onChange={onChange}/>
					<input type="submit" value={ newAcc ? "Create Account" : "Login" } />
				</form>

				<button name="google" className='btn btn-primary' onClick={onGoogleClick}>구글 계정으로 로그인</button>
			</Modal.Body>
			<Modal.Footer>
				<Button onClick={props.onHide}>Close</Button>
			</Modal.Footer>
		</Modal>
	)
}



export default App;
