import { Form, Button, Row, Col, Accordion, Card } from 'react-bootstrap';
import React, { useState }from 'react';
import DatePicker from "react-datepicker";
import axios from 'axios';

import no from './img/noimage.png';


import './App.css';
import "react-datepicker/dist/react-datepicker.css";

function Search() {
    
    var [genre, genre_change] = useState(['드라마', 'SF/판타지', '공포', '로맨스', '모험', '스릴러', '범죄/느와르', '다큐멘터리', '코미디', '가족', '미스터리/서스펜스', '전쟁', '애니메이션', '뮤지컬', '액션/무협', '에로', '영화음악', '기타'])
    const [startDate, setStartDate] = useState(new Date());
    const [searchResult, searchResult_change] = useState([])
    const [showResult, showResult_change] = useState(false);

    
    return (
        <div className='container mt-4'>
            <div className='search-box'>
                <Form>
                    <Form.Group as={Row} className="mb-3" controlId="">
                        <Form.Label column sm={2} style={{borderRight : '1px solid white'}}>Title</Form.Label>
                        <Col sm={10}>
                            <Form.Control type="text" placeholder="Search..." />
                        </Col>
                    </Form.Group>
                    
                        
                    <Accordion>
                        <Accordion.Item eventKey="0" >
                            <Accordion.Header className='search-detail'>Search Detail</Accordion.Header>
                            <Accordion.Body className='search-detail'>
                                <Form.Group as={Row} className="mb-3 genreGroup" controlId="">
                                    <Form.Label column sm={2} style={{ borderRight : '1px solid #212529'}}>장르</Form.Label>
                                    <Col sm={{ span: 10}} style={{textAlign : 'left'}}>
                                        {
                                            genre.map(function(g, i ){
                                                return <Form.Check inline label={g} className="genreName" key={i}/>
                                            })
                                        }
                                    </Col>
                                </Form.Group>
                                <Form.Group as={Row} className="mb-3 genreGroup" controlId="">
                                    <Form.Label column sm={2} style={{ borderRight : '1px solid #212529'}}>개봉연도</Form.Label>
                                    <Col sm={{ span: 10}} className='mt-2 search-year'>
                                        <div className="datepicker-content">
                                            <DatePicker 
                                                selected={startDate}
                                                onChange={(date) => setStartDate(date)}
                                                showYearPicker
                                                dateFormat="yyyy"/>
                                        </div>
                                        <div className="datepicker-style datepicker-content" > 년 ~</div>
                                        <div className="datepicker-content">
                                            <DatePicker 
                                                selected={startDate}
                                                onChange={(date) => setStartDate(date)}
                                                showYearPicker
                                                dateFormat="yyyy"/>
                                        </div>
                                        <div className="datepicker-style datepicker-content" > 년</div>
                                    </Col>
                                </Form.Group>
                            </Accordion.Body>
                        </Accordion.Item>
                    </Accordion>

                    <Button variant="primary" className="mt-3 search-result" onClick={() => {

                        axios.get('/public/searchList')
                        .then(res => {
                            searchResult_change(res.data.result);
                            showResult_change(true);
                            // console.log(searchResult);
                        });
                        
                    }}
                    >Search</Button>
                </Form>

            </div>
            
            {
                showResult
                ? <SearchResult result={searchResult}/>
                : null
            }
            
        </div>
    )
}

function SearchResult(props) {

    var [searchResult, searchResult_change] = useState(props.result);
    return (
        <div className='container mt-5'>
            <div>
                <h2 style={{float:'left', margin:'0'}}>Search Result</h2>
                <p style={{float:'right'}}>총 <span className='h3 text-primary'>{searchResult.length}</span> 개</p>
            </div>
            <div style={{clear : 'both'}}></div>
            <hr style={{marginTop : '0'}}/>

            <div className='row searchResult-list'>
                <Row>
                    {
                        searchResult.map(function(m, i) {
                            return <Movie source={m} key={i}/>
                        })
                    }
                </Row>
            </div>
        </div>
    )
}


function Movie(props) {


    return (
        <div>
            <Card sm={3} className="searchReuslt_movie">
                <Card.Img variant="top" src={ props.source.image === "" ? no : props.source.image } />
                <Card.Body>
                    <Card.Title>{props.source.title}</Card.Title>
                    <Card.Title>{props.source.subtitle}</Card.Title>
                    <Card.Text>{props.source.director}</Card.Text>
                    <Card.Text>{props.source.actor}</Card.Text>
                    <Button variant="primary" href={props.source.link}>Detail</Button>
                </Card.Body>
            </Card>
        </div>
    )

}


export default Search;
