import { Navbar, Container, Nav, Card, DropdownButton, Dropdown } from 'react-bootstrap';
import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './App.css';
import './main.css';
import jquery from 'jquery';
import $ from 'jquery';



function App() {

	var [top10, top10_change] = useState([1, 2, 3]);
	var [page, page_change] = useState(1);
	var [info, info_change] = useState(false);

	useEffect(() => {
		axios.get('https://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json', {
			params : {
				key : '36fcb6727085802f6401dc96e6743cf4',
				targetDt : 20211002,
			},
			// headers : headers,
		}).then(resonse => {
			var json = resonse.data.boxOfficeResult.dailyBoxOfficeList

			top10_change(json);
			// console.log(resonse.data.boxOfficeResult.dailyBoxOfficeList);
		}).catch( error => {
			console.log(error);
		})

	}, [page]);


	return (
		<div className="App">
			<Navbar bg="dark" variant="dark">
				<Container className='text-center'>
					<Navbar.Brand href="#home">MOVIEDIC</Navbar.Brand>
						<Nav className="me-auto">
						<Nav.Link href="#home">HOME</Nav.Link>
						<Nav.Link href="#features">BOXOFFICE</Nav.Link>
						<Nav.Link href="#pricing">HISTORY</Nav.Link>
						<Nav.Link href="#pricing">MY</Nav.Link>
					</Nav>
				</Container>
			</Navbar>


			{/* <CaroselSet /> */}


			<div className=" mt-4">
				<h4 className="text-center">TODAY BOXOFFICE</h4>
				<div className="boxoffice mt-4">
					<div className='boxoffice-cardset'>
						{
							top10.map((movie, i) => {
								return (
									<Card className="boxoffice-card" key={i}>

										<Card.Img variant="top" className="boxoffice-img" src="https://movie-phinf.pstatic.net/20211001_121/16330536363187liBB_JPEG/movie_image.jpg" />
										<Card.Text className={'boxoffice-rank card-' + i}>{movie.rank}</Card.Text>

												{	movie.rankOldAndNew === "NEW"
													? 
													<Card.Text className='boxoffice-oldandnew'>{movie.rankOldAndNew}</Card.Text>
													: null
												}
										{
											info
											? <Card.Body className="boxoffice-bg">
												<Card.Title className="boxoffice-title">{movie.movieNm}</Card.Title>

												<Card.Text className='boxoffice-content'>금일 관객 수 : {movie.audiCnt} 명</Card.Text>
												<Card.Text className='boxoffice-content'>누적 관객 수 : {movie.audiAcc} 명</Card.Text>
												<DropdownButton id="dropdown-basic-button" title="예매하기">
													<Dropdown.Item href="#/action-1">CGV</Dropdown.Item>
													<Dropdown.Item href="#/action-2">LOTTECINEMA</Dropdown.Item>
													<Dropdown.Item href="#/action-3">MEGA BOX</Dropdown.Item>
												</DropdownButton>
											</Card.Body>
											: null
										}
									</Card>
								)
							})
						}
					</div>
					<div className='boxoffice-btn'>
						<button className="btn before" onClick={() => {

						}}>&lt;</button>
						<button className="btn next" onClick={() => {

						}}>&gt;</button>
					</div>
				</div>

			</div>
		</div>
	);
}

