import { Navbar, Container, Nav, Card, Button, Carousel } from 'react-bootstrap';
import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './App.css';
import './main.css';
import jquery from 'jquery';
import $ from 'jquery';



function App() {

	var [top10, top10_change] = useState([1, 2, 3]);
	var [page, page_change] = useState(1);
	useEffect(() => {
		axios.get('https://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json', {
			params : {
				key : '36fcb6727085802f6401dc96e6743cf4',
				targetDt : 20211002,
			},
			// headers : headers,
		}).then(resonse => {
			var json = resonse.data.boxOfficeResult.dailyBoxOfficeList

			top10_change(json);
			console.log(resonse.data.boxOfficeResult.dailyBoxOfficeList);
		}).catch( error => {
			console.log(error);
		})

	}, [page]);


	return (
		<div className="App">
			<Navbar bg="dark" variant="dark">
				<Container className='text-center'>
					<Navbar.Brand href="#home">MOVIEDIC</Navbar.Brand>
						<Nav className="me-auto">
						<Nav.Link href="#home">HOME</Nav.Link>
						<Nav.Link href="#features">BOXOFFICE</Nav.Link>
						<Nav.Link href="#pricing">HISTORY</Nav.Link>
						<Nav.Link href="#pricing">MY</Nav.Link>
					</Nav>
				</Container>
			</Navbar>


			{/* <CaroselSet /> */}


			<div className="container mt-4">
				<h4 className="text-center">List</h4>
				<div className="boxoffice mt-4">
					{
						top10.map((movie, i) => {
							return (
								<Card className="boxoffice-card" onMouseOver={()=> {

								}} key={i}>
									<Card.Img variant="top" className="boxoffice-img" src="https://movie-phinf.pstatic.net/20211001_121/16330536363187liBB_JPEG/movie_image.jpg" />

									<Card.Body className="boxoffice-bg">
										<Card.Title className="boxoffice-title">{movie.movieNm}</Card.Title>
										<Card.Text className='boxoffice-rank'>{movie.rank}</Card.Text>

										<Card.Text>{movie.rankOldAndNew}</Card.Text>
										<Card.Text>{movie.audiInten}</Card.Text>
										<Card.Text>{movie.audiAcc}</Card.Text>
										<Button variant="primary">Go somewhere</Button>
									</Card.Body>
								</Card>
							)
						})
					}
				</div>

			</div>
		</div>
	);
}

